var v 		= require('Validate-alpha'),
	layout 	= require('Layout');
/*
 * Cost Controller
 */
CostController = {
	items: function(options){
		layout.LoadContent('accounts/cost-items', options);
	},
	add: function(options){
		layout.LoadContent('accounts/cost-invoice', options);
	},
	all: function(options){
		layout.LoadContent('accounts/cost-invoices', options);
	}
}
module.exports = CostController;
