var v 		= require('Validate-alpha'),
	layout 	= require('Layout');
/*
 * Purchase Controller
 */
PurchaseController = {
	suppliers: function(options){
		layout.LoadContent('accounts/suppliers', options);
	},
	add: function(options){
		layout.LoadContent('accounts/purchase-invoice', options);
	},
	all: function(options){
		layout.LoadContent('accounts/purchase-invoices', options);
	}
}
module.exports = PurchaseController;
