var v 			= require('Validate-alpha'),
	Measurement = require('DB.Modules/MeasurementUnits'),
	layout 		= require('Layout');
/*
 * Store Controller
 */
StoreController = {
	measurementUnits: function(options){
		layout.LoadContent('accounts/measurement-units', options);
	},
	inventoryItems: function(options){
		Measurement.all('', function(items){
			options.items = items;
			layout.LoadContent('accounts/inventory-items', options);
		});
	}
}
module.exports = StoreController;
