var mysql = require('mysql');

/*
 * Data Base Engine
 */
var Database = {
	_database: "",
	db: function(){
		if (!this._database) {
			this._database = mysql.createConnection({
				database 			: 'nwjs_healthcare',
				host 				: 'localhost', // '192.168.1.50',
				user 				: 'root',
				password 			: 'root',
				multipleStatements 	: true,
				dateStrings 		: true
			});
			this._database.connect(function(err) {
				if (err) return err;
				console.log("DB connected");
			});
			return this._database;
		} else {
			return this._database;
		};
	}
}
module.exports = Database;
