/*
 * Layout Engine
 */
var j = require('jade');

var Layout = {
	LoadSidebar: function(name) {
		options = {}
		var html = j.renderFile('./assets/views/sidebars/'+ name +'.jade', options);
		doc.getElementById('main-sidebar').innerHTML = html;
	},
	LoadContent: function(name, options) {
		if (typeof options === "undefined") options = {};
		var html = j.renderFile('./assets/views/'+ name +'.jade', options);
		doc.getElementById('main-content').innerHTML = html;
	},
	LoadPage: function(name) {
		options = {}
		var html = j.renderFile('./assets/views/pages/'+ name +'.jade', options);
		doc.getElementById('inside-page').innerHTML = html;
	},
	Load: function(name, options){
		options = options || {}
		var html = j.renderFile('./assets/views/layout/'+ name +'.jade', options);
		doc.getElementById('main-structure').innerHTML = html;
	},
	Columns: function() {
		options = {}
		var html = j.renderFile('./assets/views/layout/columns.jade', options);
		doc.getElementById('inside-page').innerHTML = html;
	}
}
module.exports = Layout;
